function loadWorldData(data_type, detection_type, time_period, country_id, callback, error_callback) {

    if (time_periods.indexOf(time_period) == -1)
        time_period = "w";

    if (detection_types.indexOf(detection_type) == -1)
        detection_type = "oas";


    var loadingUrl = 'data/securelist/' + data_type + '_' + detection_type + '_' + time_period +
        ((data_type == 'country') ? '' : '_' + country_id) +
        '.json';

    $.ajax({
        url: loadingUrl,
        dataType: 'json',
        async: false
    }).done(function (data) {
        if(data["data_loader_error"] == "true") {
            error_callback();
        }
        else {
            callback(data, detection_type, time_period)
        }
    }).fail(function () {
        error_callback();
    });
}

function loadCountryList() {
    $.ajax({
        url: 'map/data/countries.json',
        dataType: 'json',
        async: false
    }).done(function (data) {
        if(data["data_loader_error"] == true) {
            error_callback();
        }
        else {
            for (i in data) {
                var key = data[i].key;
                var lang_code = data[i].name;

                if (key == 0)
                    continue;

                if (disabledCountries.indexOf(key) != -1)
                    continue;

                var found = false;
                for (i = 0; i < continents.length; i++) {
                    for (j = 0; j < continents[i].countries.length; j++) {
                        if (key == continents[i].countries[j]) {
                            found = true;
                            break;
                        }
                    }
                    if (found)
                        break;
                }

                if (!found)
                    continue;

                continentSelector = "";
                for (i = 0; i < continents.length; i++) {
                    if (continents[i].countries.indexOf(parseInt(key)) != -1) {
                        continentSelector = continents[i].selector;
                        break;
                    }
                }

                countryList.push({id: key, name: window.lang.getText(lang_code), continent: continentSelector});
            }
        }

        countryList.sort(function (a, b) {
            if (a.name > b.name) {
                return 1;
            }
            if (a.name < b.name) {
                return -1;
            }
            return 0;
        });
    });
}

function drawWorldCountryRatings(data, detection_type, time_period) {
    // create array of objects with countries' info
    var countryStats = new Array();
    for (id in data) {

        countries = countryList.filter(function (c) {
            return c.id == id;
        });


        if (countries.length > 0) {
            countryStats.push({
                id: countries[0].id,
                continent: countries[0].continent,
                name: countries[0].name,
                value: parseFloat(data[id])
            });
        }
    }

    // sort objects by value
    countryStats.sort(function (a, b) {
        return b.value - a.value;
    });

    // update worlds stats
    selectedCountries = countryStats.filter(function (cn) {
        return cn.continent != "";
    });
    $("#world_stats_world .world_stats_country_list").html("");
    for (i = 0; i < 20; i++) {
        $("#world_stats_world .world_stats_country_list").html($("#world_stats_world .world_stats_country_list").html() +
            "<tr class='country_link' data-country-id='" + selectedCountries[i].id + "'>" +
            "<td class='element_index'>" + (i + 1) + "</td>" +
            "<td class='element_name'>" + selectedCountries[i].name + "</td>" +
            "<td class='element_value'>" + selectedCountries[i].value + (detection_type != "bad" ? "%" : "") + "</td>" +
            "</tr>"
        );
    }

    for (c = 0; c < continents.length; c++) {

        selected = 0;
        selectedCountries = countryStats.filter(function (cn) {
            return cn.continent == continents[c].selector;
        });

        $("#world_stats_" + continents[c].selector + " .world_stats_country_list").html("");
        for (i in selectedCountries) {
            selected++;

            $("#world_stats_" + continents[c].selector + " .world_stats_country_list").html($("#world_stats_" + continents[c].selector + " .world_stats_country_list").html() +
                "<tr class='country_link' data-country-id='" + selectedCountries[i].id + "'>" +
                "<td class='element_index'>" + (selected) + "</td>" +
                "<td class='element_name'>" + selectedCountries[i].name + "</td>" +
                "<td class='element_value'>" + selectedCountries[i].value + (detection_type != "bad" ? "%" : "") + "</td>" +
                "</tr>"
            );

            if (selected >= 5)
                break;
        }
    }

    $(".country_link").on("click", function (event) {
        countryListClick($(this), event)
    });
}

